import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import "./expenses.css";



function Expenses(props) {
  const [enteredYear, setEnteredYear] = useState('2019');
  const yearWiseData = (choosenYear) => {
    setEnteredYear(choosenYear);
    console.log(choosenYear);
  };

  return (
    <div>
    <Card className="expenses">
    {console.log(enteredYear)}
      <ExpensesFilter selected={enteredYear} toHandleYear={yearWiseData} />
      {props.items.filter(yearr => yearr.date.getYear() == enteredYear).map((expense) => (
       
       
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />


      ))}
     
    </Card>
    </div>
  );
}

export default Expenses;
