import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import "./expenses.css";

function Expenses(props) {
  const [enteredYear, setEnteredYear] = useState("2019");
  const yearWiseData = (choosenYear) => {
    setEnteredYear(choosenYear);
  };

  const filteredExpenses = props.items.filter((yearr) =>{
    yearr.date.getFullYear().toString() === enteredYear
})

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected={enteredYear} toHandleYear={yearWiseData} />

         {filteredExpenses.length === 0 ? ( <p>No expense found</p>) : (filteredExpenses.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          )))}
        
      </Card>
    </div>
  );
}

export default Expenses;
