import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import "./expenses.css";



function Expenses(props) {
  const [enteredYear, setEnteredYear] = useState('2019');
  const yearWiseData = (choosenYear) => {
    setEnteredYear(choosenYear);
  };

  return (
    <div>
    <Card className="expenses">
  
      <ExpensesFilter selected={enteredYear} toHandleYear={yearWiseData} />
      {props.items.map((expense => expense.filter(yearr => yearr.date.getYear() === enteredYear)) (

        <ExpenseItem
          key={yearr.id}
          title={yearr.title}
          amount={yearr.amount}
          date={yearr.date}
        />


      ))}
     
    </Card>
    </div>
  );
}

export default Expenses;
