import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import "./expenses.css";


let choosenYear;

function Expenses(props) {
  const [enteredYear, setEnteredYear] = useState('2019');
  const yearWiseData = (choosenYear) => {
    setEnteredYear(choosenYear);
  };

  const filteredExpenses = props.items.filter(yearr =>{ yearr.date.getYear() === choosenYear});

  return (
    <div>
    <Card className="expenses">
  
      <ExpensesFilter selected={enteredYear} toHandleYear={yearWiseData} />
      {filteredExpenses.map((expense) => (
       
       
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />


      ))}
     
    </Card>
    </div>
  );
}

export default Expenses;
